var Snowflakes = (function(document, window, Math) {
    function Flakes(params) {
        if (this instanceof Flakes) {
            this._setParams(params);
            this._initValues();
            this.start();
        } else {
            return new Flakes(params);
        }
    }

    Flakes.prototype = {
        start: function() {
            var that = this;
            if (!this._interval) {
                this._interval = setInterval(function() {
                    that.update();
                }, this.params.delay);
            }
        },
        stop: function() {
            if (this._interval) {
                clearInterval(this._interval);
                this._interval = null;
            }
        },
        update: function() {
            var width = this._width(),
                height = this._height();

            this._flakes.forEach(function(flake, i) {
                this._y[i] += this._addY[i];
                this._deltaX[i] += this._addX[i];
                this._angle[i] += this._addAngle[i];

                if (this._y[i] >= height - flake.height) {
                    this._resetValues(i, width, height);
                }

                flake.move(
                    this._x[i] + this._sinWidth[i] * Math.sin(this._deltaX[i]),
                    this._y[i],
                    this._angle[i]
                );
            }, this);
        },
        destroy: function() {
            this.stop();
            this._flakes.forEach(function(flake) {
                flake.destroy();
            });

            this._flakes = [];
        },
        _setParams: function(params) {
            params = params || {};

            this.params = {
                container: params.container || document.body,
                count: params.count || 30,
                delay: params.delay || 20,
                width: params.width,
                height: params.height
            };
        },
        _initValues: function() {
            this._flakes = [];

            this._deltaX = [];
            this._x = [];
            this._y = [];

            this._addX = [];
            this._addY = [];

            this._angle = [];
            this._addAngle = [];

            this._sinWidth = [];

            var width = this._width(),
                height = this._height();

            for (var i = 0; i < this.params.count; i++) {
                this._flakes.push(new Flake(this.params.container));
                this._resetValues(i, width, height);
                this._y[i] = -Math.random() * height;
            }
        },
        _resetValues: function(i, width, height) {
            var flake = this._flakes[i];
            this._y[i] = -flake.height;
            this._angle[i] = 0;

            this._sinWidth[i] = Math.random() * 20;
            this._deltaX[i] = 0;
            this._addY[i] = 0.7 + Math.random();
            this._addX[i] = this._addY[i] / 20;
            this._x[i] = Math.random() * width - this._sinWidth[i] - flake.width;
            this._addAngle[i] = Math.random() * 5;
        },
        _isBody: function(el) {
            return el === document.body;
        },
        _width: function() {
            var p = this.params;

            return p.width ||
                (this._isBody(p.container) ? this._winWidth() : p.container.offsetWidth);
        },
        _height: function() {
            var p = this.params;

            return p.height ||
                (this._isBody(p.container) ? this._winHeight() : p.container.offsetHeight);
        },
        _winWidth: function() {
            return window.innerWidth || document.body.clientWidth;
        },
        _winHeight: function() {
            var height,
                body = document.body,
                docElement = document.documentElement;

            if (window.innerHeight) {
                height = window.innerHeight;
            } else if (docElement && docElement.clientHeight) {
                height = docElement.clientHeight;
            } else if (body) {
                height = body.clientHeight;
            }

            return height;
        }
    };

    function Flake(container) {
        var el = document.createElement('div'),
            innerEl = document.createElement('div'),
            width = this.width + 'px',
            height = this.height + 'px',
            num = Math.floor(Math.random() * this.spriteWidth / this.width),
            left = '-' + (num * this.width) + 'px';

        el.className = 'snowflake';
        setStyle(el, {
            position: 'absolute',
            left: '-' + width,
            top: '-' + height,
            width: width,
            height: height,
            overflow: 'hidden'
        });

        setStyle(innerEl, {
            position: 'absolute',
            left: '0',
            top: '0',
            width: width,
            height: height,
            background: 'url("' + this.spriteSrc + '") no-repeat ' + left + ' 0'
        });

        el.appendChild(innerEl);
        container.appendChild(el);
        this._el = el;
        this._innerEl = innerEl;
    }

    var flakeWidth = 16,
        flakeHeight = 18;

    Flake.prototype = {
        spriteSrc: 'data:image/png;base64,',
        spriteWidth: 144,
        width: flakeWidth,
        height: flakeHeight,
        move: function(left, top, angle) {
            setStyle(this._el, {
                left: left + 'px',
                top: top + 'px'
            });

            this._innerEl.style.transform = 'rotate(' + angle + 'deg)';
        },
        destroy: function() {
            this._el.parentNode && this._el.parentNode.removeChild(this._el);
            this._el = null;
            this._innerEl = null;
        }
    };

    function setStyle(elem, props) {
        Object.keys(props).forEach(function(key) {
            elem.style[key] = props[key];
        });
    }

    return Flakes;
})(document, window, Math);
